import React from "react";
import axios from "axios";
import { BASE_URL } from "../../constants/urls";
import { useEffect, useState, useContext, createContext } from "react";
import Footer from "../../components/Footer/Footer";
import Header from "../../components/Header/Header";
import { useNavigate } from "react-router-dom";
import {
  AddressContainer,
  RestaurantContainer,
  AdressDelivery,
  AdressContainer,
  CartContainer,
  Title,
  ShippingText,
  SubtotalPrice,
  TotalPrice,
  PaymentMethodText,
  ProductContainer,
  ProductImageContainer,
  ProductInfoContainer,
  QuantityContainer,
  ProductInfo,
  RemoveButtonContainer,
  RemoveButton,
  ButtonContainer,
  PaymentContainer,
  Quantity,
  CheckBox,
  PlaceholderCtn,
} from "./styled";
import { getProfile, getDetailRestaurant } from "../../axiosRequests/user";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";
import {
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
} from "@material-ui/core";
import CardFood from "../../components/CardFood/CardFood";
import { Button, Checkbox } from "@material-ui/core";
import { RestaurantMenu } from "@material-ui/icons";
import { useRequestData } from "../../hooks/useRequestData";
import CardFoodInCart from "../../components/CardFoodInCart/CardFoodInCart";
import cartEmpty  from '../../assets/empty-cart.png'

const CartPage = () => {
  const [address, setAddress] = useState({});
  const { states, sets } = useContext(GlobalStateContext);
  const [totalPrice, setTotalPrice] = useState(0);
  const [product, setProduct] = useState([]);
  const [payment, setPayment] = useState("");
  const [shipping, setShipping] = useState([]);
  const navigate = useNavigate();
  const [restaurant, setRestaurant] = useState([]);
  const [
    restaurantDetails,
    isLoadingRestaurantDetails,
    errorRestaurantDetails,
  ] = useRequestData(`${BASE_URL}/restaurants/${states.restaurantId}`);



  const removeItem = (item) => {
    const newArray = product.filter((pdt) => {
      return pdt.id !== item.id;
    });
    setProduct(newArray);
  };

  useEffect(() => {
    getProfile(setAddress);
    getDetailRestaurant(states.cart.idRestaurant, setRestaurant);
  }, []);

  const renderAddress = address.address ? (
    <AddressContainer>
      <p>Endereço de entrega:</p>
      <span>{address.address}</span>
    </AddressContainer>
  ) : (
    <AdressContainer>
      <p>
        Favor cadastrar um endereçoclicando{" "}
        <strong onClick={() => navigate("/endereco")}>aqui.</strong>
      </p>
    </AdressContainer>
  );


const listProductsInCart = states.cart && states.cart.map((product) => {


    const toRemove = () => {
        const updateProductsInCart =
          states.cart &&
          states.cart
            .map((item) => {
              if (item.id === product.id) {
                //  return item.quantity = item.quantity -1
                return {
                  ...item,
                  quantity: item.quantity - 1,
                };
              }
              return item;
            })
            .filter((item) => {
              return item.quantity > 0;
            });

        sets.setCart(updateProductsInCart);

        console.log("REMOVI", states.cart);
      };

    return(
        <CardFoodInCart
          product={product}
          key={product.id}
         senQuantity={product.quantity}
          onClickRemove={toRemove}

          />
    )
})


  return (
    console.log("CARRINHO", states.cart),
    console.log("RESTAURANTE", restaurant),
    (
      <>
        <Header />
        <CartContainer>
          {renderAddress}

          {restaurantDetails && restaurantDetails.restaurant && (
          <RestaurantContainer>
        <p>{restaurantDetails.restaurant.name}</p>
        <span>{restaurantDetails.restaurant.address}</span>
        <span>{restaurantDetails.restaurant.deliveryTime}</span>
    </RestaurantContainer>)}

    {states.cart && states.cart.length > 0 ?  listProductsInCart : <PlaceholderCtn>
        <h3>O seu carrinho está vazio</h3>
        <img src={cartEmpty} alt="Carrinho vazio"/>
        </PlaceholderCtn>}


          {restaurantDetails && restaurantDetails.restaurant && (
          <ShippingText>
            <p>Frete:</p>
            {/* <p>{restaurantDetails.restaurant.shipping}</p> */}
          </ShippingText>
          )}

          {states.cart && states.cart.length > 0 && (
          <SubtotalPrice>
            <p>Subtotal:</p>
            <p>
              {states.cart.reduce((pdt) => {
                // return pdt.price * pdt.quantity + Number(`${restaurantDetails.restaurant.shipping}`);
              }, 0)}
            </p>
          </SubtotalPrice>
          )}

          {states.cart && states.cart.length > 0 && (
          <PaymentContainer>
            <p>Forma de pagamento</p>
            <FormControl component="fieldset">
              <RadioGroup
                aria-label="payment"
                name="payment"
                value={payment}
                onChange={(e) => setPayment(e.target.value)}
              >
                <FormControlLabel
                  value="dinheiro"
                  control={<Radio />}
                  label="Dinheiro"
                />
                <FormControlLabel
                  value="cartao"
                  control={<Radio />}
                  label="Cartão"
                />
              </RadioGroup>
            </FormControl>
          </PaymentContainer>
          )}

          {states.cart && states.cart.length > 0 && (
          <ButtonContainer>
            <Button
              variant="contained"
              color="primary"
              fullWidth
              style={{ margin: "10px" }}
            >
              Confirmar
            </Button>
          </ButtonContainer>
          )}


        </CartContainer>
        <Footer />
      </>
    )
  );
};

export default CartPage;
