import React, { useContext, useEffect, useState } from "react";
import Button from "@material-ui/core/Button";
// import Badge from "@material-ui/core/Badge";
import Typography from "@material-ui/core/Typography";
import {
  CardContainer,
  CardText,
  ColorTxt,
  ButtonCnt,
  IconBtn,
  Badge,
} from "./styled";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import ModalQuantityFood from "../ModalQuantityFood/ModalQuantityFood";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";

const CardFood = ({
  senQuantity,
  prodSelected,
  product,
  quantity,
  addCart,
  onChangeQuantity,
  onClickAdd,
}) => {
  const { states, sets } = useContext(GlobalStateContext);
  const [showBadge, setShowBadge] = useState(false);

  console.log("SENDQUANTITY", senQuantity);

  useEffect(() => {}, [states.cart]);

  // badgeContent={senQuantity.length > 0  ? senQuantity : 0}

  return (
    <div>
      <CardContainer>
        <img src={product.photoUrl} alt={product.name} />

        <CardText>
          {/* <IconBtn>
            <Badge
              color="primary"
              badgeContent={senQuantity.length > 0 ? senQuantity : 0}
              showZero
            >
              <ShoppingCartIcon color="secondary" />
            </Badge>
          </IconBtn> */}

          {senQuantity.length > 0 ? (
            <IconBtn>
              <ShoppingCartIcon color="secondary" />
              <Badge>{senQuantity}</Badge>
            </IconBtn>
          ) : (
            <IconBtn>
              <ShoppingCartIcon color="secondary" />
            </IconBtn>
          )}

          <Typography color="primary" variant="h5">
            {product.name}
          </Typography>
          <ColorTxt>
            <Typography variant="subtitle1" color="text.secondary">
              {product.description}
            </Typography>
          </ColorTxt>
          <Typography variant="h6">R$: {product.price}</Typography>
          <ButtonCnt>
            <Button onClick={onClickAdd} color="primary" variant="outlined">
              Adicionar
            </Button>
          </ButtonCnt>
        </CardText>
        {prodSelected && prodSelected.id && states.openModal && (
          <ModalQuantityFood
            quantity={quantity}
            onChangeQuantity={onChangeQuantity}
            product={prodSelected}
            addCart={addCart}
          />
        )}
      </CardContainer>
    </div>
  );
};

export default CardFood;
