import React from "react";
import {
  FooterCard,
  Item1,
  Item2,
  Item3,
  HomepageIcon,
  ShoppingCartIcon,
  AvatarIcon,
} from "./Styled";
import homepage from "../../assets/footer/homepage.svg";
import shoppingCart from "../../assets/footer/shopping-cart.svg";
import avatar from "../../assets/footer/avatar.svg";
import { useNavigate, useLocation } from "react-router-dom";

const Footer = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const goToHomepage = () => {
    navigate("/");
  };

  const goToCart = () => {
    navigate("/carrinho");
  };

  const goToProfile = () => {
    navigate("/perfil");
  };

  const changeIconBasedOnLocation = () => {
    if (location.pathname === "/") {
      return homepage2;
    } else if (location.pathname === "/carrinho") {
      return shoppingCart2;
    } else if (location.pathname === "/perfil") {
      return avatar2;
    }
  };

  return (
    <FooterCard>
      <Item1>
        <HomepageIcon src={homepage} onClick={goToHomepage} />
      </Item1>
      <Item2>
        <ShoppingCartIcon src={shoppingCart} onClick={goToCart} />
      </Item2>
      <Item3>
        <AvatarIcon src={avatar} onClick={goToProfile} />
      </Item3>
    </FooterCard>
  );
};

export default Footer;
