import React, { useContext } from "react";
import FeedCard from "../../components/FeedCard/FeedCard";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";
import { useNavigate } from "react-router-dom";
import Footer from "../../components/Footer/Footer";
import { ConteinerInput, PageContainer, ConteinerNav, SectionNavbar } from "./styled";
import { InputAdornment, TextField } from "@material-ui/core";
import { Search } from "@material-ui/icons";
// import Navbar from "../../components/Navbar/Navbar";
import Header from "../../components/Header/Header";


const FeedPage = () => {
  const { states, sets } = useContext(GlobalStateContext);
  const navigate = useNavigate();


  const goToSearch = () => {
    navigate(`/busca`);
  };
  const goToRestaurantDetail = (id) => {
    navigate(`/restaurante/${id}`);
  };

  const filterByCategory = (category) => {
    sets.restaurants(states.restaurants.filter((item) => item.category === category));
  };





  return (
    <div>

      <Header />
      <PageContainer>

        <ConteinerInput>
        <TextField
          variant="outlined"
          onClick={() => goToSearch(navigate)}
          placeholder={"  Restaurante"}
          InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <Search />
            </InputAdornment>
          ),
        }}
        />
        </ConteinerInput>

        <nav>
        <SectionNavbar >
          <ConteinerNav onClick={() => filterByCategory("brasileira")}>Brasileira</ConteinerNav>
          <ConteinerNav onClick={() => filterByCategory("chinesa")}>Chinesa</ConteinerNav>
          <ConteinerNav onClick={() => filterByCategory("italiana")}>Italiana</ConteinerNav>
          <ConteinerNav onClick={() => filterByCategory("japonesa")}>Japonesa</ConteinerNav>
          <ConteinerNav onClick={() => filterByCategory("francesa")}>Francesa</ConteinerNav>
          <ConteinerNav onClick={() => filterByCategory("india")}>India</ConteinerNav>
          <ConteinerNav onClick={() => filterByCategory("portuguesa")}>Portuguesa</ConteinerNav>
          <ConteinerNav onClick={() => filterByCategory("vegetariana")}>Vegetariana</ConteinerNav>

        </SectionNavbar>
      </nav>

        {states.restaurants &&
          states.restaurants.map((restaurant, i) => (
            <FeedCard
              key={i}
              restaurants={restaurant}
              onClickRestaurant={() => goToRestaurantDetail(restaurant.id)}
            />
          ))}
      </PageContainer>
      <Footer />
    </div>
  );
};
export default FeedPage;
