import React, { useContext, useState } from "react";
import FeedCard from "../../components/FeedCard/FeedCard";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";
import { useNavigate } from "react-router-dom";
import Footer from "../../components/Footer/Footer";
import { ConteinerInput, PageContainer, ConteinerNav, SectionNavbar } from "./styled";
import { InputAdornment, TextField } from "@material-ui/core";
import { Search } from "@material-ui/icons";
// import Navbar from "../../components/Navbar/Navbar";
import Header from "../../components/Header/Header";


const FeedPage = () => {
  const { states, sets } = useContext(GlobalStateContext);
  const navigate = useNavigate();
  const [filterclick, setFilterclick] = useState(false);


  const goToSearch = () => {
    navigate(`/busca`);
  };
  const goToRestaurantDetail = (id) => {
    navigate(`/restaurante/${id}`);
  };

  // filter restaurants by category but dont change the original state
  // const filteredRestaurants = [...states.restaurants];

  const filterByCategory = (category) => {
    sets.setFilteredRestaurants(states.restaurants);
    if (category === "all") {
      sets.setFilteredRestaurants(states.restaurants);
    }
    sets.setFilteredRestaurants(states.filteredRestaurants.filter((item) => item.category === category));
    console.log(states.restaurants);
  };






  //   sets.setRestaurants(states.restaurants)
  //   const newRestaurants = states.restaurants.filter((item) => {
  //     return item.category === category;
  //   });
  //   sets.setRestaurants(newRestaurants);
  // };





  return (
    <div>

      <Header />
      <PageContainer>

        <ConteinerInput>
        <TextField
          variant="outlined"
          onClick={() => goToSearch(navigate)}
          placeholder={"  Restaurante"}
          InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <Search />
            </InputAdornment>
          ),
        }}
        />
        </ConteinerInput>

        <nav>
        <SectionNavbar >
          <ul >
          <li><strong onClick={() => filterByCategory("Árabe")}>Árabe</strong></li>
          <li><strong onClick={() => filterByCategory("Asiática")}>Asiática</strong></li>
          <li><strong onClick={() => filterByCategory("Hamburguer")}>Hambúrguer</strong></li>
          <li><strong onClick={() => filterByCategory("Italiana")}>Italiana</strong></li>
          <li><strong onClick={() => filterByCategory("Sorvetes")}>Carnes</strong></li>
          <li><strong onClick={() => filterByCategory("Baiana")}>Baiana</strong></li>
          <li><strong onClick={() => filterByCategory("Petiscos")}>Petiscos</strong></li>
          <li><strong onClick={() => filterByCategory("Mexicaníssimo")}>Mexicaníssimo</strong></li>
          <li><strong onClick={() => filterByCategory("Todas")}>Todas</strong></li>
          </ul>
        </SectionNavbar>
      </nav>

        {states.filteredRestaurants &&
          states.filteredRestaurants.map((restaurant, i) => (
            <FeedCard
              key={i}
              restaurants={restaurant}
              onClickRestaurant={() => goToRestaurantDetail(restaurant.id)}
            />
          ))}
      </PageContainer>
      <Footer />
    </div>
  );
};
export default FeedPage;
