import React, { useContext } from "react";
import FeedCard from "../../components/FeedCard/FeedCard";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";
import { useNavigate } from "react-router-dom";
import Footer from "../../components/Footer/Footer";
import { ConteinerInput, PageContainer, ConteinerNav, SectionNavbar } from "./styled";
import { InputAdornment, TextField } from "@material-ui/core";
import { Search } from "@material-ui/icons";
// import Navbar from "../../components/Navbar/Navbar";
import Header from "../../components/Header/Header";


const FeedPage = () => {
  const { states, sets } = useContext(GlobalStateContext);
  const navigate = useNavigate();


  const goToSearch = () => {
    navigate(`/busca`);
  };
  const goToRestaurantDetail = (id) => {
    navigate(`/restaurante/${id}`);
  };

  const filterByCategory = (category) => {
    sets.setRestaurants(states.restaurants.filter((item) => item.category === category));
  };





  return (
    <div>

      <Header />
      <PageContainer>

        <ConteinerInput>
        <TextField
          variant="outlined"
          onClick={() => goToSearch(navigate)}
          placeholder={"  Restaurante"}
          InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <Search />
            </InputAdornment>
          ),
        }}
        />
        </ConteinerInput>

        <nav>
        <SectionNavbar >
          <ul >
          <li> strong onClick={() => filterByCategory("Árabe")}>Árabe</li>
          <li> strong onClick={() => filterByCategory("Asiática")}>Asiática</li>
          <li> strong onClick={() => filterByCategory("Hamburguer")}>Hambúrguer</li>
          <li> strong onClick={() => filterByCategory("Italiana")}>Italiana</li>
          <li> strong onClick={() => filterByCategory("Sorvetes")}>Carnes</li>
          <li> strong onClick={() => filterByCategory("Baiana")}>Baiana</li>
          <li> strong onClick={() => filterByCategory("Petiscos")}>Petiscos</li>
          <li> strong onClick={() => filterByCategory("Mexicaníssimo")}>Mexicaníssimo</li>
          <li> strong onClick={() => filterByCategory("todas")}>Todas</li>
          </ul>
        </SectionNavbar>
      </nav>

        {states.restaurants &&
          states.restaurants.map((restaurant, i) => (
            <FeedCard
              key={i}
              restaurants={restaurant}
              onClickRestaurant={() => goToRestaurantDetail(restaurant.id)}
            />
          ))}
      </PageContainer>
      <Footer />
    </div>
  );
};
export default FeedPage;
