import React from 'react';
import { useEffect, useState, useContext } from 'react';
import Footer from '../../components/Footer/Footer';
import Header from '../../components/Header/Header';
import { AddressContainer, RestaurantContainer, AdressDelivery,
    AdressContainer,
    CartContainer,
    Title,
    ShippingText,
    SubtotalPrice,
    TotalPrice,
    PaymentMethodText,
    CheckBox,} from './styled';
import {
    getProfile,
  } from "../../axiosRequests/user";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";
import { useCartCtx } from "../"
import {
    FormControl,
    FormControlLabel,
    Radio,
    RadioGroup,
  } from "@material-ui/core";
  import CardFood from '../../components/CardFood/CardFood';
  import { Button } from '@material-ui/core';

const CartPage = () => {
    const [address, setAddress] = useState({});
    const { states, sets } = useContext(GlobalStateContext);
    const [totalPrice, setTotalPrice] = useState(0);
  const { products, setProducts, shipping } = useCartCtx();

  const removeItem = (item) => {
    const newArray = products.filter((pdt) => {
      return pdt.id !== item.id;
    });
    setProducts(newArray);
  };

  const price = products.reduce(function (prev, pdt) {
    return pdt.price * pdt.quantity + prev;
  }, 0);


    useEffect(() => {
        getProfile(setAddress);
        setTotalPrice(price);
      }, [products]);





    return (
        <div>
            <Header />
            <CartContainer>
      <AdressContainer>
        <AdressDelivery>Endereço de entrega</AdressDelivery>

        <p>Buscando endereço...</p>
      </AdressContainer>

      <Title>Carrinho vazio</Title>

      {/* container teste para receber os produtos */}
      {products &&
        products.map((pdt) => {
          return (
            <CardFood
              photoUrl={pdt.photoUrl}
              name={pdt.name}
              description={pdt.description}
              price={pdt.price}
              quantity={pdt.quantity}
              nameButton="rem"
              onClickButtonRemove={() => removeItem(pdt)}
            />
          );
        })}

      <ShippingText>
        Frete R${" "}
        {shipping &&
          shipping.reduce(function (a, b) {
            return a + b.shipping;
          },0).toFixed(2)}
      </ShippingText>
      <SubtotalPrice>
        <p>SUBTOTAL</p>
        <TotalPrice>R$ {totalPrice.toFixed(2)}</TotalPrice>
      </SubtotalPrice>

      <PaymentMethodText>Forma de pagamento</PaymentMethodText>
      <CheckBox>
        <FormControl component="fieldset" required={true}>
          <RadioGroup name="metodoDePag" value="">
            <FormControlLabel
              value="dinheiro"
              control={<Radio color="black" />}
              label="Dinheiro"
            />
            <FormControlLabel
              value="credito"
              control={<Radio color="black" />}
              label="Cartão de crédito"
            />
          </RadioGroup>
        </FormControl>
      </CheckBox>
      <Button text="CONFIRMAR" onClick={""}></Button>
      <Footer />
    </CartContainer>
            {/* <AddressContainer>
            <p>Endereço de entrega:</p>
            <span>{address.address}</span>
          </AddressContainer>

          {states.cart.map(product => (
              <RestaurantContainer>
                  <p>{product.name}</p>
                    <p>{product.price}</p>
                </RestaurantContainer>
            ))} */}



            <Footer />
        </div>
      );
}

export default CartPage;
