import React from 'react';
import { useEffect, useState, useContext, createContext } from 'react';
import Footer from '../../components/Footer/Footer';
import Header from '../../components/Header/Header';
import { useNavigate } from 'react-router-dom';
import { AddressContainer, RestaurantContainer, AdressDelivery,
    AdressContainer,
    CartContainer,
    Title,
    ShippingText,
    SubtotalPrice,
    TotalPrice,
    PaymentMethodText,
    ProductContainer,
    ProductImageContainer,
    ProductInfoContainer,
    QuantityContainer,
    ProductInfo,

    CheckBox,} from './styled';
import {
    getProfile,
  } from "../../axiosRequests/user";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";
import {
    FormControl,
    FormControlLabel,
    Radio,
    RadioGroup,
  } from "@material-ui/core";
  import CardFood from '../../components/CardFood/CardFood';
  import { Button, Checkbox } from '@material-ui/core';

const CartPage = () => {
    const [address, setAddress] = useState({});
    const { states, sets } = useContext(GlobalStateContext);
    const [totalPrice, setTotalPrice] = useState(0);
    const [product, setProduct] = useState([]);
  const [payment, setPayment] = useState("");
  const [shipping, setShipping] = useState([]);
  const navigate = useNavigate();

  const removeItem = (item) => {
    const newArray = product.filter((pdt) => {
      return pdt.id !== item.id;
    });
    setProduct(newArray);
  };

//   const price = states.cart.price.reduce(function (prev, pdt) {
//     return pdt.price * pdt.quantity + prev;
//   }, 0);


    useEffect(() => {
        getProfile(setAddress);
        // setTotalPrice(price);
      }, []);

const renderAddress =  address.address ? (
    <AddressContainer>
            <p>Endereço de entrega:</p>
            <span>{address.address}</span>
          </AddressContainer>
    ) : (
    <AdressContainer>
        <p>Favor cadastrar um endereçoclicando <strong onClick={() => navigate("/endereco")}>aqui.</strong></p>
    </AdressContainer>
    );


    return (
        console.log("CARRINHO", states.cart),
        <>
        <Header />
        <CartContainer>
            {renderAddress}

            <RestaurantContainer>
                <p>Restaurant name</p>
                <span>Restaurant Address</span>
                <span>Delivery time</span>
            </RestaurantContainer>




            <Title>Produtos</Title>

                {states.cart.map((item) => {
                    return (
                        <ProductContainer>
                            <ProductImageContainer>
                                <img src={item.image} alt="product" />
                            </ProductImageContainer>
                            <ProductInfoContainer>
                                <QuantityContainer>
                                    <span>{item.quantity}</span>
                                </QuantityContainer>
                                <ProductInfo>
                                <p>{item.name}</p>
                                <p>Preço: {item.price}</p>
                                <p>Quantidade: {item.quantity}</p>
                                </ProductInfo>
                            <RemoveButtonContainer>
                                <RemoveButton onClick={() => removeItem(item)}>
                                    <img src={require("../../assets/images/icons/delete.svg")} alt="delete" />
                                </RemoveButton>
                            </RemoveButtonContainer>

                            </ProductInfoContainer>
                            </ProductContainer>
                    );
                }
                )}

            <SubtotalPrice>
                <p>Subtotal:</p>
                <p>{states.cart.reduce((prev, pdt) => {
                    return pdt.price * pdt.quantity + prev;
                }
                , 0)}</p>
            </SubtotalPrice>
            <ShippingText>
                <p>Frete:</p>
                <p>{shipping}</p>
            </ShippingText>
            <TotalPrice>
                <p>Total:</p>
                <p>{states.cart.reduce((prev, pdt) => {
                    return pdt.price * pdt.quantity + prev;
                }
                , 0) + shipping}</p>
            </TotalPrice>

            <Title>Forma de pagamento</Title>
            <FormControl component="fieldset">
                <RadioGroup aria-label="payment" name="payment" value={payment} onChange={(e) => setPayment(e.target.value)}>
                    <FormControlLabel value="dinheiro" control={<Radio />} label="Dinheiro" />
                    <FormControlLabel value="cartao" control={<Radio />} label="Cartão" />
                </RadioGroup>
            </FormControl>

            <PaymentMethodText>
                <p>Forma de pagamento: <strong>{payment}</strong></p>

            </PaymentMethodText>

            <Button variant="contained" color="primary">
                Finalizar
            </Button>
        </CartContainer>
        <Footer />
        </>
    );
};

export default CartPage;
