import React, { useContext } from "react";
import FeedCard from "../../components/FeedCard/FeedCard";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";
import { useNavigate } from "react-router-dom";
import Footer from "../../components/Footer/Footer";
import { ConteinerInput, PageContainer, ConteinerNav, SectionNavbar } from "./styled";
import { InputAdornment, TextField } from "@material-ui/core";
import { Search } from "@material-ui/icons";
// import Navbar from "../../components/Navbar/Navbar";
import Header from "../../components/Header/Header";


const FeedPage = () => {
  const { states, sets } = useContext(GlobalStateContext);
  const navigate = useNavigate();


  const goToSearch = () => {
    navigate(`/busca`);
  };
  const goToRestaurantDetail = (id) => {
    navigate(`/restaurante/${id}`);
  };

const arabe = "arabe";
const chines = "chines";
const italian = "italian";
const japones = "japones";
const mexicano = "mexicano";
const portugues = "portugues";
const venezolano = "venezolano";
const americano = "americano";
const asiatico = "asiatico";

const filterRestaurants = (restaurants, filter) => {
  switch (filter) {
    case arabe:
      return restaurants.filter((restaurant) => restaurant.food_type === arabe);
    case chines:
      return restaurants.filter((restaurant) => restaurant.food_type === chines);
    case italian:
      return restaurants.filter((restaurant) => restaurant.food_type === italian);
    case japones:
      return restaurants.filter((restaurant) => restaurant.food_type === japones);
    case mexicano:
      return restaurants.filter((restaurant) => restaurant.food_type === mexicano);
    case portugues:
      return restaurants.filter((restaurant) => restaurant.food_type === portugues);
    case venezolano:
      return restaurants.filter((restaurant) => restaurant.food_type === venezolano);
    case americano:
      return restaurants.filter((restaurant) => restaurant.food_type === americano);
    case asiatico:
      return restaurants.filter((restaurant) => restaurant.food_type === asiatico);
    default:
      return restaurants;
  }
};



  return (
    <div>
      <Header />
      <PageContainer>

        <ConteinerInput>
        <TextField
          variant="outlined"
          onClick={() => goToSearch(navigate)}
          placeholder={"  Restaurante"}
          InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <Search />
            </InputAdornment>
          ),
        }}
        />
        </ConteinerInput>

        <nav>
        <SectionNavbar >
          <ul >
            {/* click on strong to filter restaurants */}
            <li> <strong
            onClick={() => filterNavbar(arabe)}>Árabe</strong>
            </li>
            <li ><strong>Asiática</strong></li>
            <li ><strong>Hamburguer</strong></li>
            <li ><strong>Italiana</strong></li>
            <li ><strong>Sorvetes</strong></li>
            <li ><strong>Carnes</strong></li>
            <li ><strong>Baiana</strong></li>
            <li ><strong>Petiscos</strong></li>
            <li ><strong>Mexicana</strong></li>
          </ul>
        </SectionNavbar>
      </nav>

        {states.restaurants &&
          states.restaurants.map((restaurant, i) => (
            <FeedCard
              key={i}
              restaurants={restaurant}
              onClickRestaurant={() => goToRestaurantDetail(restaurant.id)}
            />
          ))}
      </PageContainer>
      <Footer />
    </div>
  );
};
export default FeedPage;
