import React, { useContext } from "react";
import FeedCard from "../../components/FeedCard/FeedCard";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";
import { useNavigate } from "react-router-dom";
import Footer from "../../components/Footer/Footer";
import { ConteinerInput, PageContainer, ConteinerNav, SectionNavbar } from "./styled";
import { InputAdornment, TextField } from "@material-ui/core";
import { Search } from "@material-ui/icons";
// import Navbar from "../../components/Navbar/Navbar";
import Header from "../../components/Header/Header";


const FeedPage = () => {
  const { states, sets } = useContext(GlobalStateContext);
  const navigate = useNavigate();
  const goToSearch = () => {
    navigate(`/busca`);
  };
  const goToRestaurantDetail = (id) => {
    navigate(`/restaurante/${id}`);
  };

  const filterNavbar = (e) => {
    e.preventDefault();
    const  value  = e.target.toString();
    const lowerValue = value.toLowerCase();
    const filteredRestaurants = states.restaurants.filter((item) => {
      return item.name.toLowerCase().includes(lowerValue);
    });
    return filteredRestaurants;
  };


  return (
    <div>
      <Header />
      <PageContainer>

        <ConteinerInput>
        <TextField
          variant="outlined"
          onClick={() => goToSearch(navigate)}
          placeholder={"  Restaurante"}
          InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <Search />
            </InputAdornment>
          ),
        }}
        />
        </ConteinerInput>

        <nav>
        <SectionNavbar >
          <ul >
            {/* click on strong to filter restaurants */}
            <li onClick={(e) => {
              e.preventDefault();
              const filteredRestaurants = filterNavbar(e);
              sets.setRestaurants(filteredRestaurants);
            }}>
              <strong>Árabe</strong>
            </li>
            <li ><strong>Asiática</strong></li>
            <li ><strong>Hamburguer</strong></li>
            <li ><strong>Italiana</strong></li>
            <li ><strong>Sorvetes</strong></li>
            <li ><strong>Carnes</strong></li>
            <li ><strong>Baiana</strong></li>
            <li ><strong>Petiscos</strong></li>
            <li ><strong>Mexicana</strong></li>
          </ul>
        </SectionNavbar>
      </nav>

        {states.restaurants &&
          states.restaurants.map((restaurant, i) => (
            <FeedCard
              key={i}
              restaurants={restaurant}
              onClickRestaurant={() => goToRestaurantDetail(restaurant.id)}
            />
          ))}
      </PageContainer>
      <Footer />
    </div>
  );
};
export default FeedPage;
