import React from 'react';
import { useEffect, useState, useContext, createContext } from 'react';
import Footer from '../../components/Footer/Footer';
import Header from '../../components/Header/Header';
import { AddressContainer, RestaurantContainer, AdressDelivery,
    AdressContainer,
    CartContainer,
    Title,
    ShippingText,
    SubtotalPrice,
    TotalPrice,
    PaymentMethodText,
    CheckBox,} from './styled';
import {
    getProfile,
  } from "../../axiosRequests/user";
import { GlobalStateContext } from "../../components/Global/GlobalStateContext";
import {
    FormControl,
    FormControlLabel,
    Radio,
    RadioGroup,
  } from "@material-ui/core";
  import CardFood from '../../components/CardFood/CardFood';
  import { Button } from '@material-ui/core';

const CartPage = () => {
    const [address, setAddress] = useState({});
    const { states, sets } = useContext(GlobalStateContext);
    const [totalPrice, setTotalPrice] = useState(0);
    const [products, setProducts] = useState([]);
  const [payment, setPayment] = useState("");
  const [shipping, setShipping] = useState([]);

  const removeItem = (item) => {
    const newArray = products.filter((pdt) => {
      return pdt.id !== item.id;
    });
    setProducts(newArray);
  };

//   const price = states.cart.price.reduce(function (prev, pdt) {
//     return pdt.price * pdt.quantity + prev;
//   }, 0);


    useEffect(() => {
        getProfile(setAddress);
        // setTotalPrice(price);
      }, []);

const renderAddress =  address.address ? (
    <AddressContainer>
            <p>Endereço de entrega:</p>
            <span>{address.address}</span>
          </AddressContainer>
    ) : (
    <AdressContainer>
        <p>Não há endereço de entraga, favor cadastrar um clicando aqui:</p>
        <Button variant="contained" color="primary">
            Cadastrar
        </Button>
    </AdressContainer>
    );


    return (
        <>
        <Header />
        <CartContainer>
            <Title>Endereço de entrega</Title>
            {renderAddress}
            <Title>Forma de pagamento</Title>
            <FormControl component="fieldset">
                <RadioGroup aria-label="payment" name="payment" value={payment} onChange={(e) => setPayment(e.target.value)}>
                    <FormControlLabel value="dinheiro" control={<Radio />} label="Dinheiro" />
                    <FormControlLabel value="cartao" control={<Radio />} label="Cartão" />
                </RadioGroup>
            </FormControl>
            <Title>Forma de envio</Title>
            <FormControl component="fieldset">
                <RadioGroup aria-label="shipping" name="shipping" value={shipping} onChange={(e) => setShipping(e.target.value)}>
                    <FormControlLabel value="sedex" control={<Radio />} label="Sedex" />
                    <FormControlLabel value="pac" control={<Radio />} label="PAC" />
                </RadioGroup>
            </FormControl>
            <Title>Produtos</Title>
            <RestaurantContainer>
                {states.cart.map((item) => {
                    return (
                        <CardFood
                            key={item.id}
                            item={item}
                            removeItem={removeItem}
                        />
                    );
                }
                )}
            </RestaurantContainer>
            <SubtotalPrice>
                <p>Subtotal:</p>
                <p>{states.cart.reduce((prev, pdt) => {
                    return pdt.price * pdt.quantity + prev;
                }
                , 0)}</p>
            </SubtotalPrice>
            <ShippingText>
                <p>Frete:</p>
                <p>{shipping}</p>
            </ShippingText>
            <TotalPrice>
                <p>Total:</p>
                <p>{states.cart.reduce((prev, pdt) => {
                    return pdt.price * pdt.quantity + prev;
                }
                , 0) + shipping}</p>
            </TotalPrice>
            <PaymentMethodText>
                <p>Forma de pagamento:</p>
                <p>{payment}</p>
            </PaymentMethodText>
            <CheckBox>
                <FormControlLabel
                    control={<Checkbox checked={true} />}
                    label="Aceito os termos de uso"
                />
            </CheckBox>
            <Button variant="contained" color="primary">
                Finalizar
            </Button>
        </CartContainer>
        <Footer />
        </>
    );
};

export default CartPage;
